package AfterOops;

public class RecurssionFactorialwithinstance {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 RecurssionFactorialwithinstance A=new  RecurssionFactorialwithinstance();
		 A.fac();
		 System.out.println(sum);
		

	}
	int a=5;
	static int sum=1;
	int i=1;
	private void fac() {
		// TODO Auto-generated method stub
		if(i<=a)
		{
			sum=sum*a;
			a--;
			fac();
		
	}

	
}
}